import numpy as npimport scipy.stats as statsimport matplotlib.pyplot as pltimport seaborn as snsf = open('re.txt','r')data = f.readlines()data1 = []list = []v = []h = []f.close()del data[0]for i in range(len(data)):    data[i] = data[i].replace('\n','').split('\t')    for j in range(len(data[i])):        data[i][j] = float(data[i][j])data = np.array(data)mean_v = np.mean(data[:, 2])mean_h = np.mean(data[:, 3])plt.figure()plt.xlabel("velocity(cm/s)")sns.distplot(data[:,2], bins=7)plt.figure()plt.subplot(1,2,1)plt.xlabel('hoop height<cm>')plt.ylabel("probability")sns.distplot(data[:,3], bins=5)plt.subplot(1,2,2)plt.ylabel('hoop height<cm>')plt.boxplot(data[:,3])plt.show()print("mean_h:\t"+str(mean_h)+'\n')print("mean_v:\t"+str(mean_v)+'\n')